import {useState, useEffect, useCallback, useContext} from 'react';
import { update } from "../BooksAPI";
import { Context } from "./Context";
import { getAll } from "../BooksAPI";

function Books(props) {
    const [OptionValue, setOption] = useState(props.book.shelf);
    const { AllBooksData, setBooks } = useContext(Context);

    const OptionUpdateInDB = (event) => {
      console.log('Shelf : '+OptionValue)
      if(OptionValue !== ''){
        update(props.book, OptionValue)
        AllBooks();
      }
    }

    const OptionChanged = (event) => {
      setOption(event.target.value);
    }

    useEffect(() => {
      OptionUpdateInDB()
    }, [OptionValue]);

    const AllBooks = () => {
      getAll()
        .then(response => {
          return response.json()
        })
        .then(data => {
          setBooks(data.books);
        })
    }

    function getAuthours(){
      if(props.book.authors?.length == 1){
          return(
            <span key={1}>
                {props.book.authors} <br></br>
            </span>
          )
        }
        else if (props.book.authors?.length > 1){
          props.book.authors.map((author, index) => {
            return(
              <span key={index}>
                  {author} <br></br>
              </span>
            )
          })
        }
      }

    const autohrs = getAuthours();

    return (
      <li>
        <div className="book">
          <div className="book-top">
            <div
              className="book-cover"
              style={{
                width: 128,
                height: 193,
                backgroundImage: `url(${props.book.imageLinks.smallThumbnail})`,
              }}
            ></div>
            <div className="book-shelf-changer">
              <select value={OptionValue} onChange={OptionChanged}>
                <option value="none" disabled>
                  Move to...
                </option>
                <option value="currentlyReading">Currently Reading</option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </div>
          </div>
          <div className="book-title">{props.book.title}</div>
          <div className="book-authors">
            {autohrs}
          </div>
        </div>
      </li>
    );
  }

 export default Books;
