import "./App.css";
import { useState, useEffect } from "react";
import BookShelf from "./Components/BookShelf";
import { getAll } from "./BooksAPI";
import React from 'react'
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [AllBooksData, setBooks] = useState([]);
  const AllBooks = () => {
    getAll()
      .then(response => {
        return response.json()
      })
      .then(data => {
        setBooks(data.books)
      })
  }
  useEffect(() => {
    AllBooks()
  }, [])
  const CurrentlyReadingBooks = GetBooksBasedShelf("currentlyReading", AllBooksData);
  const WantTpReadingBooks = GetBooksBasedShelf("wantToRead", AllBooksData);
  const ReadBooks = GetBooksBasedShelf("read", AllBooksData);
  console.log(CurrentlyReadingBooks);
  console.log(WantTpReadingBooks);
  console.log(ReadBooks);
  window.cr = CurrentlyReadingBooks;

  return (
    <BrowserRouter>
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
            <BookShelf data={{ Shelf_title: 'Currently Reading', books: CurrentlyReadingBooks }}/> 
             <BookShelf data={{ Shelf_title: 'Want to Read', books: WantTpReadingBooks }}/> 
             <BookShelf data={{ Shelf_title: 'Read', books: ReadBooks }}/> 
            </div>
          </div>
          <div className="open-search">
          <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          <Link to=""><button>Search</button></Link>
          <Routes>
            <Route path="/Search" />
          </Routes>
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
    </BrowserRouter>
  );
}

function GetBooksBasedShelf(ShelfName, AllBooks){
  let resultBooks = [];
    AllBooks.map(book => {
        if(book.shelf === ShelfName)
          resultBooks.push(book);
      })
  return resultBooks;
}

export default App;
