import React from 'react'
import { Link } from 'react-router-dom';
import { useState, useCallback, useEffect } from "react";
import { search } from "../BooksAPI";
import debounce from 'lodash.debounce';
import Books from './Books'

const api = "https://reactnd-books-api.udacity.com";

let token = localStorage.token;
const headers = {
    Accept: "application/json",
    Authorization: token,
  };

function Search() {
    const [SearchResult, setSearchResult] = useState([]);
    const [QueryValue, setSearchInput] = useState('');


   /* const SearchQuery = (event) => {
       // console.log('Value : '+ event.target.value)
        let resultBooksArr = [];
        let SearchResultBooks = search(event.target.value);
        SearchResultBooks.then((data) => {
            for(let i =0; i<data.length; i++){
                resultBooksArr.push(data[i]);
            }
        });
        setSearchResult(resultBooksArr);
    }
    console.log(SearchResult)
    const debouncedSearchQuery= useCallback(
        debounce(SearchQuery, 300)
      , []);
    */

    const setSearchQuery = (event) => {
        setSearchInput(event.target.value);
        SearchQuery();
    }
   // console.log(QueryValue)
    const SearchQuery = () => {
        // console.log('Value : '+ event.target.value)
        let query = QueryValue;
        let maxResults = 17;
        if(query !== '' ){
            fetch(`${api}/search`, {
                method: "POST",
                headers: {
                ...headers,
                "Content-Type": "application/json",
                },
                body: JSON.stringify({ query, maxResults }),
            })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setSearchResult(data.books)
            })
            console.log(SearchResult)

        }
     }
   /* const debouncedSearchQuery= useCallback(
         debounce(setSearchQuery, 300)
    , []);
*/
    const debouncedSearchQuery = useEffect(() => {
        setSearchQuery();
    }, []);
    return (
        <div className="search-books">
        <div className="search-books-bar">
            <Link className="close-search" to="/"> Close</Link>

        <div className="search-books-input-wrapper">
            <input
            type="text"
            placeholder="Search by title, author, or ISBN"
            onChange={debouncedSearchQuery}
            />
        </div>
        </div>
        <div className="search-books-results">
        <ol className="books-grid">
           {/*  {SearchResult.map((book) => (
                <Books key={book.id} book={book}></Books>
            ))} */}
        </ol>
        </div>
    </div>
    )
}


export default Search;
