import React from 'react'
import { Link } from 'react-router-dom';
import { useState, useCallback } from "react";
import { search } from "../BooksAPI";
import debounce from 'lodash.debounce';


function Search() {
    const [SearchQueryValue, setSearchResult] = useState([]);

    const SearchQuery = (event) => {
        console.log('Value : '+ event.target.value)
        let resultBooksArr = [];
        let SearchResultBooks = search(event.target.value);
        SearchResultBooks.then((data) => {
            for(let i =0; i<data.length; i++){
                resultBooksArr.push(data[i]);
            }
        });
        setSearchResult(resultBooksArr);
        console.log(resultBooksArr)
    }
    const debouncedSearchQuery= useCallback(
        debounce(SearchQuery, 300)
      , []);

    console.log(debouncedSearchQuery);
    return (
        <div className="search-books">
        <div className="search-books-bar">
            <Link className="close-search" to="/"> Close</Link>

        <div className="search-books-input-wrapper">
            <input
            type="text"
            placeholder="Search by title, author, or ISBN"
            onChange={debouncedSearchQuery}

            />
        </div>
        </div>
        <div className="search-books-results">
        <ol className="books-grid"></ol>
        </div>
    </div>
    )
}

export default Search;
